mapper.select:
SELECT ID, NAME, ADDRESS, ROLE
FROM USERINFO
WHERE ID = #{id}

mapper.update:
UPDATE USERINFO
SET name = #{name}, role = #{role}, address = #{address}
WHERE ID = #{id}

UPDATE USERINFO
SET enabled = 'N'
WHERE ID = #{id}

mapper.select:
SELECT SEQ, BOOKTITLE, BOOKAUTHOR, BOOKPUBLISHER, RESERVER, RESDATE, DATE_ADD(RESDATE, INTERVAL 7 DAY) AS RETURNDATE
FROM reserveinfo
WHERE RESERVER = #{id}
